mapper.select:
SELECT count(id) 
FROM member 
WHERE id = #{id}

SELECT count(email) 
FROM member 
WHERE email = #{email}

SELECT count(mobno) 
FROM member 
WHERE mobno = #{mobno}


	SELECT count(name) FROM member 
	WHERE 
	
	name = #{name} 
	
	
	id = #{id} 
	
	and email = #{email}
	and status like 'nomal'

SELECT id, to_char(regDate,'YYYY-MM-DD') FROM member 
WHERE name = #{name} and email = #{email}
and status like 'nomal'
